export const addToCart = (pizza, quantity, varient) => (dispatch) =>{
    var cardItem = {
        name : pizza.name,
        _id : pizza._id,
        image : pizza.image,
        varient : varient,
        quantity : quantity,
        price : pizza.prices[0][varient] * quantity, 
    };
    dispatch({type: "ADD_TO_CART", payload: CarItem});
};
