  return (
    <>
      <Container>
        {/* {loading ? (
          <Loader />
        ) : error ? (
          <Error>Error while fetching pizzas {error}</Error>
        ) : ( */}
          <Row>
            {/* <Filters /> */}
            {AllPizza.map((pizza) => (
              <Col md={4} >
                <Pizza pizza={pizza} />
              </Col>
            ))}
          </Row>
      </Container>
    </>
  );
};

export default HomeScreen;
